import React, { useContext, useState } from "react";
import { useHistory } from "react-router-dom";
import { API } from "../../config/api";
import { Card } from "react-bootstrap";

export default function CardPokemon(props) {
  const { pokemons } = props;
  const history = useHistory();
  const [modalShow, setModalShow] = useState(false);

  // handle Page Detail
  const handleToDetail = () => {
    // state.isSubscribe ? history.push(`/detail-readpokemons/${pokemons.id}`) : setModalShow(true);
    alert("asede");
  };

  return (
    <div>
      <div onClick={handleToDetail}>
        <Card style={{ width: "13em", border: "none", borderRadius: "12px", height: "25em" }}>
          <Card.Img
            variant="top"
            src={pokemons.image}
            alt={pokemons.name}
            style={{ width: "100%" }}
          />
          <Card.Body>
            <Card.Title style={{ fontSize: "20px" }}>{pokemons?.name}</Card.Title>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
}
