import React, { useContext, useState } from "react";
import { useHistory } from "react-router-dom";
import { API } from "../../config/api";
import { Card } from "react-bootstrap";

export default function CardPokemon(props) {
  const { pokemons, key } = props;
  const history = useHistory();
  const [modalShow, setModalShow] = useState(false);

  // handle Page Detail
  const handleToDetail = (name) => {
    // state.isSubscribe ? history.push(`/detail-readpokemons/${pokemons.id}`) : setModalShow(true);
    history.push(`/detail-pokemons/${name}`);
  };

  return (
    <div onClick={() => handleToDetail(pokemons.name)}>
      <Card key={key} style={{ width: "13em", borderRadius: "12px" }}>
        <Card.Img
          variant="top"
          src={pokemons.image}
          alt={pokemons.name}
          style={{ width: "100%" }}
        />
        <Card.Body>
          <Card.Title style={{ fontSize: "20px" }}>{pokemons?.name}</Card.Title>
        </Card.Body>
      </Card>
    </div>
  );
}
