import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { API } from "../../config/api";
import DetailCss from "./Detail.module.css";

export default function DetailPokemon() {
  const history = useHistory();
  const { name } = useParams();
  const [pokemon, setPokemon] = useState("");
  const [isChecked, setIsChecked] = useState(false);

  // const check = async () => {
  //   try {
  //     const check = await API.get("/myList");
  //     const checkBool = check.data.data.filter((item) => {
  //       return item.Pokemons?.id === +id;
  //     });
  //     checkBool.length > 0 ? setIsChecked(true) : setIsChecked(false);
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  // const handleAddMyList = async () => {
  //   try {
  //     const body = {
  //       PokemonId: id,
  //     };
  //     // todo
  //     const res = await API.post("/myList", body);
  //   } catch (error) {
  //     console.log(error);
  //   }

  //   history.push("/profile");
  // };

  // get detail Pokemons

  const getPokemon = async () => {
    try {
      const response = await API.get(`https://pokeapi.co/api/v2/pokemon/${name}`);
      setPokemon(response.data);

      console.log("THIS IS DATA POKEMON ID", response.data);
    } catch (error) {
      console.log(error);
    }
  };

  // if call component
  useEffect(() => {
    getPokemon();
  }, []);

  return (
    <div
      className={DetailCss.sideDetailPokemon}
      style={{ marginLeft: "36px", marginRight: "36px", display: "flex", marginBottom: "50px" }}
    >
      <div
        calssName={DetailCss.detailContainer}
        style={{
          marginLeft: "3em",
          marginRight: "3em",
          marginTop: "3em",
        }}
      >
        <div className={DetailCss.descPokemon}>
          <div className={DetailCss.imgPokemon}>
            {/* <img src={`http://localhost:5000/uploads/${Pokemon.image}`} alt={Pokemon.image} /> */}
          </div>
          <div className={DetailCss.listDescPokemon}>
            <div className={DetailCss.listDesc}>
              <h1 className={DetailCss.title}>{pokemon.name}</h1>
              <span>{pokemon.name}</span>
            </div>
            <div className={DetailCss.listDesc}>
              <p>Publication date</p>
              <span>{pokemon.publicationDate}</span>
            </div>
            <div className={DetailCss.listDesc}>
              <p>Pages</p>
              <span>{pokemon.pages}</span>
            </div>
            <div className={DetailCss.listDesc}>
              <p className="text-danger">ISBN</p>
              <span>{pokemon.isbn}</span>
            </div>
          </div>
        </div>
        <div className={DetailCss.aboutPokemon}>
          <h1>About This Pokemon</h1>
          <div className="textPokemon">
            <p>{pokemon.name}</p>
          </div>
        </div>
      </div>
    </div>
  );
}
